mapper.select:
		 SELECT 
		 	t.d_date as dDate
    	from(
			SELECT r.patient_id, d_date
			FROM diagnosis d, reception r
			WHERE d.r_id = r.r_id
    	) t
    	WHERE 
    		t.patient_id = #{pId}
    	ORDER BY
    		t.d_date ASC;

mapper.insert:
INSERT INTO medicine_has_diagnosis
	(m_id,
	 d_id,
	 p_id
	)
VALUES(
	#{mId},
	#{dId},
	#{pId}
)
